declare const typeDefs = "\ntype Book {\n  bookId: ID!\n  authors: [String]\n  description: String!\n  image: String\n  link: String\n  title: String!\n}\n\ntype User {\n  _id: ID!\n  username: String!\n  email: String!\n  password: String!\n  savedBooks: [Book]\n  bookCount: Int\n}\n\ntype Auth {\n  token: ID!\n  user: User\n}\n\ntype Query {\n  me: User\n  getUser(username: String!): User\n}\n\ntype Mutation {\n  login(email: String!, password: String!): Auth\n  addUser(username: String!, email: String!, password: String!): Auth\n  saveBook(bookData: BookInput!): User\n  removeBook(bookId: ID!): User\n}\n\ninput BookInput {\n  bookId: ID!\n  authors: [String]\n  description: String!\n  image: String\n  link: String\n  title: String!\n}\ntype Mutation {\n  saveBook(bookData: BookInput!): User\n}\n\n\n";
export default typeDefs;
